mapper.insert:
INSERT INTO books (
	family_id, name, pdf_url, image_url, created_at, updated_at
) VALUES (
	#{familyId}, #{name}, #{pdfUrl,jdbcType=VARCHAR}, #{imageUrl,jdbcType=VARCHAR}, SYSTIMESTAMP, SYSTIMESTAMP
)

mapper.select:
SELECT 
FROM books WHERE family_id = #{familyId}
ORDER BY created_at DESC
